data "aws_ami" "frontend" {
  most_recent = true
  owners      = ["self"]

  filter {
    name   = "name"
    values = ["three-tier-frontend"]
  }

  filter {
    name   = "state"
    values = ["available"]
  }
}

data "aws_ami" "backend" {
  most_recent = true
  owners      = ["self"]

  filter {
    name   = "name"
    values = ["three-tier-backend"]
  }

  filter {
    name   = "state"
    values = ["available"]
  }
}

resource "aws_lb" "alb_app" {
  name               = "alb-app"
  internal           = true
  load_balancer_type = "application"
  security_groups    = [var.app_sg_id]
  subnets            = var.app_subnets

  enable_deletion_protection = false
  idle_timeout               = 60

  tags = {
    Name = "alb_app_${terraform.workspace}"
  }
}

resource "aws_lb_target_group" "alb_target_group_app" {
  name     = "alb-target-group-app"
  port     = 80
  protocol = "HTTP"
  vpc_id   = var.vpc_id

  health_check {
    path                = "/"
    port                = "80"
    timeout             = 5
    healthy_threshold   = 2
    unhealthy_threshold = 2
    interval            = 30
    matcher             = "200"
  }

  tags = {
    Name = "alb_target_group_app_${terraform.workspace}"
  }
}

resource "aws_alb" "alb_web" {
  name               = "alb-web"
  internal           = false
  load_balancer_type = "application"
  security_groups    = [var.alb_sg_id]
  subnets            = var.public_subnets

  enable_deletion_protection = false
  idle_timeout               = 60

  tags = {
    Name = "alb_web_${terraform.workspace}"
  }
}

resource "aws_lb_target_group" "alb_target_group_web" {
