resource "aws_lb_listener" "alb_listener_web_ssl" {
  load_balancer_arn = aws_alb.alb_web.id
  port              = 443
  protocol          = "HTTPS"

  default_action {
    type             = "forward"
    target_group_arn = aws_lb_target_group.alb_target_group_web.id
  }
}

resource "aws_lb_listener" "web_https" {
  load_balancer_arn = aws_lb.web_alb.id
  port              = 443
  protocol          = "HTTPS"
  ssl_policy        = "ELBSecurityPolicy-2016-08"
  certificate_arn   = var.certificate_arn

  default_action {
    type             = "forward"
    target_group_arn = aws_lb_target_group.web.id
  }
}

resource "aws_launch_template" "web" {
